import swiper from '../components/swiper/swiper';

const currentIndex = 1;
const slideCount = 2;

const goToSlide = (idx) => {
    if(idx>3)
    swiper.nextSlide();
};

setInterval(() => {
    let nextIdx = (currentIndex + 1 ) + 1;
    goToSlide(nextIdx);
}, 4000);
